import './App.css';
import { useEffect, useState } from "react";

function Lobby({ onJoin }) {
    const [code, setCode] = useState("");
    const [inputCode, setInputCode] = useState("");

    useEffect(() => {
        fetchLoginCode();
    }, []);

    const fetchLoginCode = async () => {
        const response = await fetch("http://localhost:8000/get_code");
        const code_json = await response.json();
        setCode(code_json.data);
    };

    const joinServer = async (joinCode) => {
        const response = await fetch("http://localhost:8000/join_server", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ code: joinCode ?? inputCode })
        });
        const join_json = await response.json();

        if ("error" in join_json) {
            alert("Error: " + join_json.error);
            return false;
        }
        onJoin();
        return true;
    };

    const createServer = async () => {
        const response = await fetch("http://localhost:8000/create_server");
        const data = await response.json();

        if ("error" in data) {
            alert("Error creating server: " + data.error);
            return;
        }

        setCode(data.join_code);
        onJoin();
    };

    return (
        <div className="App">
            {code && <p>Join Code: <strong>{code}</strong></p>}
            <button onClick={createServer}>Create Lobby</button>
            <input
                type="text"
                placeholder="join code"
                value={inputCode}
                onChange={(e) => setInputCode(e.target.value)}
            />
            <button onClick={() => joinServer()}>Join</button>
        </div>
    );
}

export default Lobby;
